import React, { useState, useCallback } from 'react';
import { Card, Button, Avatar, List, Comment, Popover } from 'antd';
import PropTypes from 'prop-types';
import Link from 'next/link';
import StoreCardImage from './StoreCardImage'
const dummyStore={
    imgSrc:null,
    storeTitle:'네도날드',
    storeAddress:'경기도 성남시 분당구 불정로 6 (우)13561',
    storeNumber:'1588-3830'
}
const dummyStore2={
    imgSrc:'',
    storeTitle:'네도날드',
    storeAddress:'경기도 성남시 분당구 불정로 6 (우)13561',
    storeNumber:'1588-3830'
}

const StoreCard=()=>{
    const [storeImg,setStoreImg]=useState(dummyStore.imgSrc);
    return(
        <Card cover={
            storeImg==null?<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />
                :<StoreCardImage setStoreImg={setStoreImg}/>}>
            <Card.Meta title={dummyStore2.storeTitle}
                       description={dummyStore.storeAddress}>
            </Card.Meta>
        </Card>
    )
}
export default StoreCard;
